INSERT INTO `wp_2_h5p_libraries_languages` VALUES (4,'ar','{\n  \"semantics\": [\n    {\n      \"label\": \"وصف النشاط\",\n      \"description\": \"وصف الكيفية التي ينبغي للمستخدم حل النشاط\"\n    },\n    {\n      \"label\": \"الحقل النصي\",\n      \"description\": \"تضاف الكلمات التي تحمل علامة أستريكس (*) في بديتهاونهايتها بدون اضافة مسافة في منتصف الكلمة. على سبيل المثال: يتم وضع علامة على الكلمات الصحيحة مع مثل هذا *كلمة* ا\"\n    },\n    {\n      \"label\": \"النص   \\\"تحقق \\\" لزر\"\n    },\n    {\n      \"label\": \"النص   \\\"إعادة المحاولة \\\" لزر\"\n    },\n    {\n      \"label\": \"النص  \\\"مشاهدة الحل \\\" لزر\"\n    },\n    {\n      \"label\": \"نص الملاحظات\",\n      \"description\": \"ملاحظات النص، والمتغيرات المتاحة:@score @total @correct @wrong @missed. مثال: \'نقاط : @score of @total, الصحيحة: @correct, الخطأ: @wrong, غائب: @missed\'\"\n    },\n    {\n      \"label\": \"حساسية الاجابة\",\n      \"description\": \"يتأكد من إجابة المستخدم بأن تكون بالضبط نفس الجواب\",\n      \"fields\": [\n        {\n          \"label\": \"تمكين زر \\\"إعادة المحاولة\\\"\"\n        },\n        {\n          \"label\": \"تمكين زر \\\"مشاهدة الحل\\\"\"\n        }\n      ]\n    },\n    {\n      \"englishLabel\": \"Correct answer text\",\n      \"label\": \"نص الجواب الصحيح\",\n      \"englishDefault\": \"Correct!\",\n      \"default\": \"صحيح!\"\n    },\n    {\n      \"englishLabel\": \"Incorrect answer text\",\n      \"label\": \"نص الجواب غير صحيح\",\n      \"englishDefault\": \"Incorrect!\",\n      \"default\": \"غير صحيح!\"\n    },\n    {\n      \"englishLabel\": \"Missed answer text\",\n      \"label\": \"غاب نص الجواب\",\n      \"englishDefault\": \"Missed!\",\n      \"default\": \"افتقد!\"\n    },\n    {\n      \"englishLabel\": \"Description for Display Solution\",\n      \"label\": \"وصف لعرض الحل\",\n      \"englishDefault\": \"Task is updated to contain the solution.\",\n      \"default\": \"يتم تحديث مهمة لاحتواء الحل.\"\n    }\n  ]\n}\n'),(4,'de','{\n  \"semantics\": [\n    {\n      \"label\": \"Aufgabenbeschreibung\",\n      \"description\": \"Beschreibe, wie der Nutzer die Aufgabe lösen kann.\",\n      \"placeholder\": \"Klicke auf alle Verben im folgenden Text.\"\n    },\n    {\n      \"label\": \"Textfeld\",\n      \"placeholder\": \"Das ist eine Antwort: *Antwort*.\",\n      \"description\": \"Markierte Wörter werden mit einem Sternchen (*) hinzugefügt. Ein Sternchen kann mit einem weiteren Sternchen maskiert werden, so dass im Text aus ** ein * wird. Beispiel: Die korrekten Wörter sind mit: *korrekteswort* markiert, und ein Sternchen als Zeichen wir so geschrieben: **.\"\n    },\n    {\n      \"label\": \"Text für den \\\"Überprüfen\\\"-Button\",\n      \"default\": \"Überprüfen\"\n    },\n    {\n      \"label\": \"Text für den \\\"Wiederholen\\\"-Button\",\n      \"default\": \"Wiederholen\"\n    },\n    {\n      \"label\": \"Text für den \\\"Lösung zeigen\\\"-Button\",\n      \"default\": \"Lüsung zeigen\"\n    },\n    {\n      \"label\": \"Rückmeldungstext\",\n      \"default\": \"Du hast @score von @total Punkten\",\n      \"placeholder\": \"Du hast @score von @total Punkten\",\n      \"description\": \"Als Variablen für den Rückmeldungstext gibt es: @score @total @correct @wrong @missed. Beispiel: \'Punkte: @score von @total, korrekt: @correct, falsch: @wrong, übergangen: @missed.\'\"\n    },\n    {\n      \"label\": \"Verhaltenseinstellungen.\",\n      \"description\": \"Diese Optionen bestimmen, wie die Aufgabe auf Eingaben reagiert.\",\n      \"fields\": [\n        {\n          \"label\": \"\\\"Wiederholen\\\" ermöglichen\"\n        },\n        {\n          \"label\": \"\\\"Lösung zeigen\\\" ermöglichen\"\n        }\n      ]\n    },\n    {\n      \"label\": \"Text für richtige Antworten\",\n      \"default\": \"Korrekt!\",\n      \"description\": \"Mit diesem Text wird angezeigt, dass die Antwort richtig war.\"\n    },\n    {\n      \"label\": \"Text für falsche Antworten\",\n      \"default\": \"Falsch!\",\n      \"description\": \"Mit diesem Text wird angezeigt, dass die Antwort falsch war.\"\n    },\n    {\n      \"label\": \"Text für übergangene Antworten\",\n      \"default\": \"Vergessen!\",\n      \"description\": \"Mit diesem Text wird angezeigt, dass die Antwort nicht gegeben wurde.\"\n    },\n    {\n      \"label\": \"Beschreibung für das Zeigen der Lösung\",\n      \"default\": \"Die Aufgabe zeigt nun die Lösungen\",\n      \"description\": \"Mit diesem Text wird angezeigt, dass nun die richtigen Lösungen angezeigt werden.\"\n    }\n  ]\n}\n'),(4,'fr','{\n  \"semantics\": [\n    {\n      \"label\": \"Consigne\",\n      \"description\": \"La consigne indique ce que l\'utilisateur doit faire.\",\n      \"placeholder\": \"Clique sur les verbes du texte.\"\n    },\n    {\n      \"label\": \"Champ de texte\",\n      \"placeholder\": \"Voici une réponse exacte : *réponse*.\",\n      \"description\": \"Les mots marqués sont précédés et suivis d\'une astérisque (*). Pour afficher un astérisque, on utilise avec un autre astéristique ; ainsi ** = *. Au final, les réponses exactes sont marquées ainsi : *motcorrect* et un astéristique est noté comme cela: **.\"\n    },\n    {\n      \"label\": \"Texte pour le bouton \\\"Vérifier\\\"\",\n      \"default\": \"Vérifier\"\n    },\n    {\n      \"label\": \"Texte pour le bouton \\\"Recommencer\\\"\",\n      \"default\": \"Recommencer\"\n    },\n    {\n      \"label\": \"Texte pour le bouton \\\"Voir la solution\\\"\",\n      \"default\": \"Voir la solution\"\n    },\n    {\n      \"label\": \"Texte de commentaire\",\n      \"default\": \"Tu as obtenu @score point(s) sur @total\",\n      \"placeholder\": \"Tu as obtenu @score point(s) sur @total\",\n      \"description\": \"Texte de commentaire, variables possibles : @score @total @correct @wrong @missed. Exemple: \'Ton score : @score sur @total (réponses justes : @correct, réponses fausses : @wrong, oublis : @missed.)\'\"\n    },\n    {\n      \"label\": \"Options générales.\",\n      \"description\": \"Ces options vous permettent de contrôler le déroulement de l\'activité.\",\n      \"fields\": [\n        {\n          \"label\": \"Activer le bouton \\\"Recommencer\\\"\"\n        },\n        {\n          \"label\": \"Activer le bouton \\\"Voir la solution\\\"\"\n        }\n      ]\n    },\n    {\n      \"englishLabel\": \"Correct answer text\",\n      \"label\": \"Texte de réponse correct\",\n      \"englishDefault\": \"Correct!\",\n      \"default\": \"Correct!\"\n    },\n    {\n      \"englishLabel\": \"Incorrect answer text\",\n      \"label\": \"Texte de réponse incorrect\",\n      \"englishDefault\": \"Incorrect!\",\n      \"default\": \"Incorrect!\"\n    },\n    {\n      \"englishLabel\": \"Missed answer text\",\n      \"label\": \"Texte de réponse manqué\",\n      \"englishDefault\": \"Missed!\",\n      \"default\": \"Manqué!\"\n    },\n    {\n      \"englishLabel\": \"Description for Display Solution\",\n      \"label\": \"Description pour la solution d\'affichage\",\n      \"englishDefault\": \"Task is updated to contain the solution.\",\n      \"default\": \"La tâche est mise à jour pour contenir la solution.\"\n    }\n  ]\n}\n'),(4,'it','{\n  \"semantics\": [\n    {\n      \"label\": \"Descrizione attività\",\n      \"description\": \"Descrivere come l\'utente deve risolvere il compito.\"\n    },\n    {\n      \"label\": \"Campo testo\",\n      \"description\": \"Le parole contrassegnate sono aggiunte con un asterisco (*). Un asterisco può essere sottratto con un altro asterisco, con conseguente ** = *. Esempio: Le parole corrette sono contrassegnate in questo modo: *parolacorretta* , e un carattere asterisco è scritto in questo modo: **.\"\n    },\n    {\n      \"label\": \"Testo per il pulsante \\\"Verifica\\\"\"\n    },\n    {\n      \"label\": \"Testo per il pulsante \\\"Riprova\\\"\"\n    },\n    {\n      \"label\": \"Testo per il pulsante \\\"Mostra Soluzione\\\"\"\n    },\n    {\n      \"label\": \"Testo del Feedback\",\n      \"description\": \"Testo del Feedback (Riscontro), variabili consentite: @score @total @correct @wrong @missed. Esempio: \'Punteggio : @score di @total, corretto: @correct, errato: @wrong, mancante: @missed.\'\"\n    },\n    {\n      \"label\": \"Impostazioni esecuzione\",\n      \"description\": \"Queste opzioni consentono di controllare l\'andamento del compito.\",\n      \"fields\": [\n        {\n          \"label\": \"Abilita \\\"Riprova\\\"\"\n        },\n        {\n          \"label\": \"Abilita il pulsante \\\"Mostra soluzioni\\\"\"\n        }\n      ]\n    },\n    {\n      \"englishLabel\": \"Correct answer text\",\n      \"label\": \"Testo della risposta corretta\",\n      \"englishDefault\": \"Correct!\",\n      \"default\": \"Corretta!\"\n    },\n    {\n      \"englishLabel\": \"Incorrect answer text\",\n      \"label\": \"Testo della risposta non corretta\",\n      \"englishDefault\": \"Incorrect!\",\n      \"default\": \"Non corretta\"\n    },\n    {\n      \"englishLabel\": \"Missed answer text\",\n      \"label\": \"Testo della risposta senza risposta\",\n      \"englishDefault\": \"Missed!\",\n      \"default\": \"Senza risposta!\"\n    },\n    {\n      \"englishLabel\": \"Description for Display Solution\",\n      \"label\": \"Descrizione per soluzione di visualizzazione\",\n      \"englishDefault\": \"Task is updated to contain the solution.\",\n      \"default\": \"Task viene aggiornato per contenere la soluzione.\"\n    }\n  ]\n}'),(4,'nb','{\n  \"semantics\":\n  [\n    {\n      \"label\": \"Oppgavebeskrivelse\",\n      \"description\": \"Forklar hvordan brukeren skal løse oppgaven.\"\n    },\n    {\n      \"label\": \"Tekstlinjer\",\n      \"placeholder\": \"Oslo er hovedstaden i *Norge*\",\n      \"description\": \"Åpne felter merkes med en stjerne * foran og bak det korrekte ordet/uttrykket. Symbolet * lages ved å bruke to etter hverandre, slik ** = *. Eks: Dette er et *svar*, og dette er en asterisk **.\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Sjekk svar\\\" knapp\",\n      \"default\": \"Sjekk\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Prøv igjen\\\" knapp\",\n      \"default\": \"Prøv igjen\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Fasit\\\" knapp\",\n      \"default\": \"Fasit\"\n    },\n    {\n      \"label\": \"Tilbakemelding\",\n      \"default\": \"@score av @total poeng\",\n      \"description\": \"Tekstlig tilbakemelding, tilgjengelige variabler: @score og @total. Eksempel: \'Du fikk @score av @total felt rett.\'\"\n    },\n    {\n      \"label\": \"Innstillinger for oppgave-oppførsel\",\n      \"description\": \"Disse instillingene lar deg bestemme hvordan oppgavetypen skal oppføre seg.\",\n      \"fields\": [\n        {\n          \"label\": \"Aktiver \\\"Prøv igjen\\\"\"\n        },\n        {\n          \"label\": \"Aktiver \\\"Fasit\\\" knapp\"\n        }\n      ]\n    },\n    {\n      \"englishLabel\": \"Correct answer text\",\n      \"label\": \"Riktig svar tekst\",\n      \"englishDefault\": \"Correct!\",\n      \"default\": \"Riktig\"\n    },\n    {\n      \"englishLabel\": \"Incorrect answer text\",\n      \"label\": \"Feil svar tekst\",\n      \"englishDefault\": \"Incorrect!\",\n      \"default\": \"Feil\"\n    },\n    {\n      \"englishLabel\": \"Missed answer text\",\n      \"label\": \"Mangler svar tekst\",\n      \"englishDefault\": \"Missed!\",\n      \"default\": \"Mangler!\"\n    },\n    {\n      \"englishLabel\": \"Description for Display Solution\",\n      \"label\": \"Beskrivelse for Vis Løsning\",\n      \"englishDefault\": \"Task is updated to contain the solution.\",\n      \"default\": \"Oppgaven er oppdatert til å inneholde fasiten.\"\n    }\n  ]\n}'),(4,'nn','{\n  \"semantics\":\n  [\n    {\n      \"label\": \"Oppgåve beskriving\",\n      \"description\": \"Forklar korleis brukeren skal løyse oppgaven.\"\n    },\n    {\n      \"label\": \"Tekstliner\",\n      \"placeholder\": \"Oslo er hovudstaden i *Noreg*\",\n      \"description\": \"Opne felter merkast med ein stjerne * foran og bak det korrekte ordet/uttrykket. Symbolet * lages ved å bruke to etter hverandre, slik ** = *. Eks: Dette er et *svar*, og dette er en asterisk **.\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Sjekk svar\\\" knapp\",\n      \"default\": \"Sjekk\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Prøv igjen\\\" knapp\",\n      \"default\": \"Prøv igjen\"\n    },\n    {\n      \"label\": \"Tekst for \\\"Fasit\\\" knapp\",\n      \"default\": \"Fasit\"\n    },\n    {\n      \"label\": \"Tilbakemelding\",\n      \"default\": \"@score av @total poeng\",\n      \"description\": \"Tekstleg tilbakemelding, tilgjengelege variabler: @score og @total. Eksempel: \'Du fikk @score av @total felt rett.\'\"\n    },\n    {\n      \"label\": \"Innstillinger for oppgåve-åtferd\",\n      \"description\": \"Disse instillingene lar deg bestemme hvordan oppgavetypen skal oppføre seg.\",\n      \"fields\": [\n        {\n          \"label\": \"Aktiver \\\"Prøv igjen\\\"\"\n        },\n        {\n          \"label\": \"Aktiver \\\"Fasit\\\" knapp\"\n        }\n      ]\n    },\n    {\n      \"englishLabel\": \"Correct answer text\",\n      \"label\": \"Riktig svar tekst\",\n      \"englishDefault\": \"Correct!\",\n      \"default\": \"Riktig\"\n    },\n    {\n      \"englishLabel\": \"Incorrect answer text\",\n      \"label\": \"Feil svar tekst\",\n      \"englishDefault\": \"Incorrect!\",\n      \"default\": \"Feil\"\n    },\n    {\n      \"englishLabel\": \"Missed answer text\",\n      \"label\": \"Mangler svar tekst\",\n      \"englishDefault\": \"Missed!\",\n      \"default\": \"Mangler!\"\n    },\n    {\n      \"englishLabel\": \"Description for Display Solution\",\n      \"label\": \"Beskrivelse for Vis Løsning\",\n      \"englishDefault\": \"Task is updated to contain the solution.\",\n      \"default\": \"Oppgaven er oppdatert til å inneholde fasiten.\"\n    }\n  ]\n}\n'),(8,'en','{\n  \"libraryStrings\": {\n    \"engli