INSERT INTO `wp_h5p_libraries_hub_cache` VALUES (1,'H5P.Accordion',1,0,10,1,5,'Accordion','Create vertically stacked expandable items','Reduce the amount of text presented to readers by using this responsive accordion. Readers decide which headlines to take a closer look at by expanding the title. Excellent for providing an overview with optional in-depth explanations.','https://h5p.org/sites/default/files/accordion.svg',1488282378,1491485431,0,8,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/accordion-01.png?itok=qnXarBZ5\",\"alt\":\"The view screenshot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/accordion-02.png?itok=lnTobl4C\",\"alt\":\"The view screenshot\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/accordion','','[\"accordion\",\"collapsible text\"]','[]','Joubel'),(2,'H5P.AppearIn',1,0,6,1,0,'Appear.in','Add a video chat to your web page','A free HTML5 based video conference content type allowing authors to set up conference rooms. Add a room for Appear.in video conversations to your site. Great for connecting your learners or community members! You may even add a video chat room as a slide to a Course Presentation.','https://h5p.org/sites/default/files/appear-in.svg',1488282690,1491485907,0,26,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/appear.in-01.png?itok=xNHrXKk5\",\"alt\":\"The view screenshot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/appear.in-02.png?itok=8Cg8QWRr\",\"alt\":\"The view screenshot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/appear.in-03.png?itok=xHdMY8KH\",\"alt\":\"The view screenshot\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/appear.in','','[\"appearin\",\"video chat\",\"webcam\",\"office hours\"]','[\"Multimedia\"]','griffen'),(3,'H5P.ArithmeticQuiz',1,0,6,1,0,'Arithmetic Quiz','Create time-based arithmetic quizzes','Create arithmetic quizzes consisting of multiple choice questions. As an author, all you have to do is decide the type and length of the quiz. Users keep track of score and time spent when solving the quiz.','https://h5p.org/sites/default/files/arithmetic%20quiz.svg',1488283418,1491464429,0,24,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-01.png?itok=w00QVe4N\",\"alt\":\"Arithmetic quiz start screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-02.png?itok=k6nal6kx\",\"alt\":\"Arithmetic quiz question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-03.png?itok=oHISx8VF\",\"alt\":\"Arithmetic quiz correct answer given\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-04.png?itok=JIcoDl58\",\"alt\":\"Arithmetic quiz end screen\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/arithmetic-quiz','','[\"math\",\"arithmetic quiz\",\"countdown\"]','[\"Games\",\"Questions\"]','Joubel'),(4,'H5P.Chart',1,2,0,1,0,'Chart','Quickly generate bar and pie charts','Need to present simple statistical data graphically without creating the artwork manually? Chart is your answer.','https://h5p.org/sites/default/files/chart.svg',1488283592,1491465031,0,27,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-01_0.png?itok=iRQawZtl\",\"alt\":\"Pie chart view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-02_0.png?itok=6G-3NLs-\",\"alt\":\"Bar chart view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/arithmetic-quiz-03_0.png?itok=dGg4KIiV\",\"alt\":\"Bar chart view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/chart','','[\"chart\",\"bar chart\",\"pie chart\",\"graph\"]','[\"Multimedia\"]','Joubel'),(5,'H5P.Collage',0,3,0,1,7,'Collage','Create a collage of multiple images','The Collage tool allows you to organize images into a soothing composition. ','https://h5p.org/sites/default/files/college.svg',1488374773,1491486908,0,23,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-04_0.png?itok=Ix3CDsjz\",\"alt\":\"Editor screenshot \"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-03_0.png?itok=idf9cgYV\",\"alt\":\"Editor screenshot with images\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-01_0.png?itok=-UtwiYRz\",\"alt\":\"End user\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/collage-02_0.png?itok=aTHRDus1\",\"alt\":\"End user\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/collage','','[\"collage\",\"image\"]','[\"Multimedia\"]','Joubel'),(6,'H5P.Column',1,4,0,1,12,'Column','Organize H5P content into a column layout','Organize your content type into a column layout with H5P Column. Content types that address similar material or share a common theme can now be grouped together to create a coherent learning experience. In addition, authors are free to be creative by combining almost all of the existing H5P content types.','https://h5p.org/sites/default/files/column.svg',1488375032,1491465690,0,17,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-01.png?itok=37ibbzhk\",\"alt\":\"The view screenshot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-02.png?itok=e2km4R4C\",\"alt\":\"The view screenshot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/column-03.png?itok=nilYMNyC\",\"alt\":\"The view screenshot\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/column','','[\"column\",\"layout\",\"compound\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(7,'H5P.CoursePresentation',1,16,0,1,12,'Course Presentation','Create a presentation with interactive slides','Course presentations consist of slides with multimedia, text, and many different types of interactions like interactive summaries, multiple choice questions and interactive videos. Learners can experience new interactive learning material and test their knowledge and memory in Course Presentations. As always with H5P, content is editable in web browsers, and the Course Presentation activity type includes a WYSIWYG drag and drop based authoring tool. A typical use of the Course Presentation activity is to use a few slides to introduce a subject and follow these with a few more slides in which the user’s knowledge is tested. Course Presentations may however be used in many different ways, including as a presentation tool for use in the classroom, or as a game where the usual navigation is replaced with navigation buttons on top of the slides to let the user make choices and see the consequences of their choices.','https://h5p.org/sites/default/files/course%20presentation.svg',1488375267,1491487154,1,2,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-01.png?itok=ilSK_sEu\",\"alt\":\"Authoring experience \\u2013 choosing an image\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-02_0.png?itok=NrkD9l5t\",\"alt\":\"Authoring experience \\u2013 adding a question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-03.png?itok=cJiwaVzi\",\"alt\":\"EditorAuthoring experience \\u2013 adding a question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-04_0.png?itok=CIQbHd-t\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-05_0.png?itok=z-6dFoL1\",\"alt\":\"User\'s view \\u2013 summary screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-06_0.png?itok=gHNw6Vt0\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/course-presentation-07_0.png?itok=oAOD70di\",\"alt\":\"User\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/presentation','https://h5p.org/tutorial-course-presentation','[\"course presentation\",\"slides\",\"powerpoint\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(8,'H5P.Dialogcards',1,5,1,1,0,'Dialog Cards','Create text-based turning cards','Dialog cards can be used as a drill to help learners memorize words, expressions or senctences. On the front of the card there\'s a hint for a word or expression. By turning the card the learner reveals a corresponding word or expression. Dialog cards can be used in language learning, to present math problems or help learners remember facts such as historical events, formulas or names.','https://h5p.org/sites/default/files/dialog%20cards%20.svg',1488375538,1491487171,1,11,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-01_0.png?itok=OgMQk8D5\",\"alt\":\"The student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-02_0.png?itok=Pv49ouxC\",\"alt\":\"The student\'s view - card turned\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-03.png?itok=8wwQDVUw\",\"alt\":\"The student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/dialog-cards-04.png?itok=8VFY5QY3\",\"alt\":\"The student\'s view - card turned\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/dialog-cards','https://h5p.org/tutorial-dialog-cards','[\"dialog cards\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(9,'H5P.DocumentationTool',1,4,0,1,0,'Documentation Tool','Create a form wizard with text export','The documentation tool aims to make it easy to create assessment wizards for goal driven activities. It can also be used as a form wizard. While editing, the author can add multiple steps to the wizard. In each step, the author can define which content goes into that step. Content can be plain text, input fields, goal definition and goal assessment. Once published, the end user will be taken through the steps of the wizard. On the last step of the wizard, the user can generate a document with all the input that has been submitted. This document can be downloaded. The Documentation tool is fully responsive and works great on smaller screens as well as on your desktop.','https://h5p.org/sites/default/files/documentation%20tool%20.svg',1488375711,1491487242,0,20,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-01.png?itok=mIhHTIX3\",\"alt\":\"User\'s view \\u2013 document your project\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-02.png?itok=WCPC1IpT\",\"alt\":\"User\'s view \\u2013 Goals\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-03.png?itok=GWEa3LZf\",\"alt\":\"User\'s view \\u2013 Done\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/documentation-tool-04.png?itok=yFf2Qa-n\",\"alt\":\"User\'s view \\u2013 Document your project\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/documentation-tool','https://h5p.org/tutorial-documentation-tool','[\"dialog cards\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(10,'H5P.DragQuestion',1,10,1,1,13,'Drag and Drop','Create drag and drop tasks with images','Drag and drop question enables the learner to associate two or more elements and to make logical connections in a visual way. Create Drag and drop questions using both text and images as draggable alternatives. H5P Drag and drop questions support one-to-one, one-to-many, many-to-one and many-to-many relations between questions and answers. ','https://h5p.org/sites/default/files/drag%20and%20drop%20.svg',1488376151,1493966181,0,7,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-01.png?itok=xS6wYXCj\",\"alt\":\"Student\'s view \\u2013 quiz\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-03.png?itok=BMTD9pmT\",\"alt\":\"Student\'s view \\u2013 quiz\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-04.png?itok=_QbzXvhm\",\"alt\":\"Student\'s view \\u2013 quiz\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-02.png?itok=xcDAfXTD\",\"alt\":\"Authoring experience\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-06.png?itok=jONaT2bA\",\"alt\":\"Student\'s view \\u2013 quiz\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-and-drop-05.png?itok=wngjR93X\",\"alt\":\"Student\'s view \\u2013 results\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/drag-and-drop','https://h5p.org/tutorial-drag-and-drop-question','[\"drag and drop\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(11,'H5P.DragText',1,5,7,1,6,'Drag the Words','Create text-based drag and drop tasks','Drag the Words allows content designers to create textual expressions with missing pieces of text. The end user drags a missing piece of text to its correct place, to form a complete expression. May be used to check if the user remembers a text she has read, or if she understands something. Helps the user think through a text. It\'s super easy to create a drag the words task. The editor just writes the text and encloses the words that are to be draggable with asterisk signs like *draggableWord*.','https://h5p.org/sites/default/files/drag%20the%20words.svg',1488376477,1494246491,1,4,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-01.png?itok=dpMCvPPe\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-02.png?itok=njr_aEqB\",\"alt\":\"Student\'s view displaying results\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/drag-the-words-03.png?itok=2xeHtKBa\",\"alt\":\"Student\'s view - displaying tip\\/hint\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/drag-the-words','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/drag-the-words','[\"drag\",\"words\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(12,'H5P.Blanks',1,7,3,1,6,'Fill in the Blanks','Create a task with missing words in a text','Learners fill in the missing words in a text. The learner is shown a solution after filling in all the missing words, or after each word depending on settings. Authors enter text and mark words to be replaced with an asterix. In addition to native and second language learning, Fill in the blanks can be used to test the learner\'s ability to reproduce facts or produce mathematical inferences.','https://h5p.org/sites/default/files/fill%20in%20the%20blanks.svg',1488376747,1491472123,0,1,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-01.png?itok=EwyBhMVL\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-02.png?itok=ByI6y9m-\",\"alt\":\"Student\'s view displaying results\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-03.png?itok=49rZ2yTY\",\"alt\":\"Student\'s view displaying tip\\/hint\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/fill-in-the-blanks-04.png?itok=m8MnJh16\",\"alt\":\"Student\'s view - automatic answer check\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/fill-in-the-blanks','https://h5p.org/tutorial-fill-in-the-blanks','[\"fill\",\"blanks\",\"text\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(13,'H5P.ImageHotspotQuestion',1,5,0,1,6,'Find the Hotspot','Create image hotspots for users to find','This content type allows end users to press somewhere on an image and get feedback on whether that was correct or incorrect according to the task description. The author uploads an image and defines various hotspots corresponding to details or sections of the image. Hotspots can either be defined as correct or incorrect, and the author provides appropriate feedback text in both cases. The author can also define a feedback if the end user presses somewhere which is neither defined as a correct nor incorrect hotspot.','https://h5p.org/sites/default/files/find%20the%20hotspot.svg',1488377636,1491487435,0,14,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-hotspot-01.png?itok=G5zJHEh0\",\"alt\":\"Student\'s view displaying results\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/find-the-hotspot-02.png?itok=mNIZ5J08\",\"alt\":\"Authoring experience - adding hotspots\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/image-hotspot-question','','[\"find\",\"hotspot\",\"image\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(14,'H5P.GuessTheAnswer',1,1,4,1,6,'Guess the Answer','Create an image with a question and answer','This content type allows authors to upload an image and add a suitable description. End users can guess the answer and press the bar below the image to reveal the correct answer.','https://h5p.org/sites/default/files/guess%20the%20answer.svg',1488377905,1491475381,0,19,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/guess-the-answer-01.png?itok=cKWhsf7W\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/guess-the-answer-02.png?itok=k9g-O8UW\",\"alt\":\"Student\'s view displaying results\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/guess-the-answer','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/guess-the-answer','[\"guess\",\"answer\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(15,'H5P.IFrameEmbed',1,0,13,1,0,'Iframe Embedder','Embed from a url or a set of files','The Iframe embedder makes it easy to make an H5P of already existing JavaScript applications.','https://h5p.org/sites/default/files/iframe%20embedder.svg',1488378484,1491475605,0,18,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/iFrame-embedder-01.png?itok=CehXfNpd\",\"alt\":\"End user\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/iFrame-embedder-02.png?itok=p5V0tHcY\",\"alt\":\"End user\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/iframe-embedder','','[\"embed\",\"iframe\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(16,'H5P.InteractiveVideo',1,16,0,1,13,'Interactive Video','Create videos enriched with interactions','Add interactivity to your video with explanations, extra pictures, tables, Fill in the Blank and multiple choice questions. Quiz questions support adaptivity, meaning that you can jump to another part of the video based on the user\'s input.  Interactive summaries can be added at the end of the video. Interactive videos are created and edited using the H5P authoring tool in a standard web browser.','https://h5p.org/sites/default/files/interactive%20video.svg',1488379462,1493820378,0,3,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-01.png?itok=WgAmEzb0\",\"alt\":\"Student\'s view - starting screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-02.png?itok=9P0-eiaB\",\"alt\":\"Authoring experience - adding question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-03.png?itok=2X03y0pe\",\"alt\":\"Authoring experience - adding question\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-04.png?itok=NtKe_dhK\",\"alt\":\"Student\'s view - showing bookmarks\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-05.png?itok=Xy-1r3Dt\",\"alt\":\"Student\'s view - drag ang drop quiz\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/interactive-video-06.png?itok=aVysZc0i\",\"alt\":\"Student\'s view - summary \"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/interactive-video','https://h5p.org/tutorial-interactive-video','[\"interactive\",\"video\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(17,'H5P.MarkTheWords',1,6,7,1,6,'Mark the Words','Create a task where users highlight words','Mark the words allows content designers to create textual expressions with a defined set of correct words. The end user highlights words according to the task description and is given a score. For the editor it is super easy to create a click the words challenge. The editor types in the text and encloses the words that the user is supposed to click, the right answers, in asterix like *correctWord*.','https://h5p.org/sites/default/files/mark%20the%20words.svg',1488379610,1491478324,0,9,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/mark-the-words-01_0.png?itok=FTe4BK8x\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/mark-the-words-03_0.png?itok=veoD8bpQ\",\"alt\":\"Student\'s view displaying results\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/mark-the-words','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/mark-the-words','[\"mark\",\"words\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(18,'H5P.MemoryGame',1,2,1,1,0,'Memory Game','Create the classic image pairing game','Create your own memory games and test the memory of your site\'s users with this simple yet beautiful HTML5 game.','https://h5p.org/sites/default/files/memmory%20game.svg',1488379742,1491477891,0,13,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-02.png?itok=wrU2ytID\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-03.png?itok=dCb0YlGe\",\"alt\":\"Student\'s view - feedback after the match\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/memmory-game-01.png?itok=AeOPD5HE\",\"alt\":\"Student\'s view - matching two different images\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/memory-game','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/memory-game','[\"memory\",\"game\",\"cards\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(19,'H5P.MultiChoice',1,9,2,1,6,'Multiple Choice','Create flexible multiple choice questions','Multiple Choice questions can be an effective assesment tool. The learner is given immediate performance feedback. The H5P Multiple Choice questions can have a single or multiple correct options per question.','https://h5p.org/sites/default/files/multiple%20choice.svg',1488379865,1491948470,0,0,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-01.png?itok=CiwAg_Sn\",\"alt\":\"Student\'s view - one correct answer\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-02.png?itok=f5hGmJWk\",\"alt\":\"Student\'s view - one correct answer\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-03.png?itok=R7zb6rTZ\",\"alt\":\"Student\'s view - multiple correct answers\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-choice-04.png?itok=eSNsVzFo\",\"alt\":\"Student\'s view - multiple correct answers\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/multichoice','https://h5p.org/tutorial-multichoice-question','[\"multiple\",\"choice\",\"quiz\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(20,'H5P.PersonalityQuiz',1,0,8,1,0,'Personality Quiz','Create personality quizzes','In this content type, the author defines a series of questions with alternatives, where each alternative is matched against one or more personalities. At the end of the quiz, the end user will see which personality matches the best. There are several ways of making this quiz visually appealing, by eg. representing questions, alternatives, and personalities using images.','https://h5p.org/sites/default/files/personality%20quiz.svg',1488380090,1491487592,0,29,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-01.png?itok=ygSGXtZF\",\"alt\":\"Student\'s view - starting screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-02.png?itok=79jwUFNT\",\"alt\":\"Student\'s view - question 1\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-03.png?itok=lDhEO4KA\",\"alt\":\"Student\'s view - question 2\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-04.png?itok=1zBGhax9\",\"alt\":\"Student\'s view - question 3\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/personality-quiz-05.png?itok=rdoNJ4-j\",\"alt\":\"Student\'s view - end screen\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/personality-quiz','','[\"personality\",\"quiz\"]','[\"Multimedia\",\"Questions\"]','Lumenia'),(21,'H5P.Questionnaire',1,1,2,1,0,'Questionnaire','Create a questionnaire to receive feedback','Gain feedback and ask open ended questions in Interactive Videos and other content types with Questionnaire. Questionnaire makes the user\'s answers available via an xAPI integration. This means that website owners may store the answers in many different ways. Answers may be stored in an LRS, the sites own custom storage or a script can fetch the e-mail address and use it to send the user an e-mail. On H5P.org answers are stored in Google Analytics.','https://h5p.org/sites/default/files/questionaire.svg',1488380236,1491487615,0,31,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-01.png?itok=zVNElQ9C\",\"alt\":\"Student\'s view - question 1\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-02.png?itok=SdSQGHbW\",\"alt\":\"Student\'s view - question 2\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-03.png?itok=sNnbfkv_\",\"alt\":\"Student\'s view - question 3\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/questionnaire-04.png?itok=J8eZGmV0\",\"alt\":\"Student\'s view - end screen\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/questionnaire','','[\"questionnaire\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(22,'H5P.QuestionSet',1,12,0,1,0,'Quiz (Question Set)','Create a sequence of various question types','Question Set is your typical quiz content type. It allows the learner to solve a sequence of various question types such as Multichoice, Drag and drop and Fill in the blanks in a Question set. As an author, there are many settings you can use to make it behave just the way you want it to. You may, for instance, customize the Question set with background images and define a pass percentage for the learner. The Question Set also allows you to add videos that are played at the end. One video for success, another if the learner fails the test. This might motivate learners to try again if they fail so that they get to see the success video.','https://h5p.org/sites/default/files/question-set.svg',1488380337,1491911175,0,5,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-01.png?itok=7xWQV7Vm\",\"alt\":\"Student\'s view - question 1\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-02_0.png?itok=ku46xHQX\",\"alt\":\"Student\'s view - question 2\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-03.png?itok=n1cnLs5j\",\"alt\":\"Student\'s view - question 3\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/question-set-04.png?itok=TrmmsdwT\",\"alt\":\"Student\'s view displaying results\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/question-set','https://h5p.org/tutorial-question-set','[\"question set\",\"collection\",\"quiz\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(23,'H5P.SingleChoiceSet',1,8,1,1,11,'Single Choice Set','Create questions with one correct answer','Single choice set allows content designers to create question sets with one correct answer per question. The end user gets immediate feedback after submitting each answer.','https://h5p.org/sites/default/files/single%20choice%20set.svg',1488380512,1491480396,0,10,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-01.png?itok=IzXoN6tp\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-02.png?itok=yrj9-jB0\",\"alt\":\"Student\'s view correct answer selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-03.png?itok=o-b5R46U\",\"alt\":\"Student\'s view finished\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/single-choice-set-04.png?itok=0BlfuOy3\",\"alt\":\"Student\'s view displaying results\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/single-choice-set','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/single-choice-set','[\"collection\",\"quiz\",\"choice\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(24,'H5P.Summary',1,7,0,1,11,'Summary','Create tasks with a list of statements','Summaries help the learner remember key information in a text, video or presentation, by actively buliding a summary about the topic at hand. When the learner has completed a summary, a complete list of key statements about the topic is shown.','https://h5p.org/sites/default/files/summary.svg',1488380637,1491480990,0,22,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-01.png?itok=ICk8qnxS\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-02.png?itok=DY9wdA0S\",\"alt\":\"Student\'s view - wrong answer selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-03.png?itok=nWaNT-Ll\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-04.png?itok=EYtv3B7n\",\"alt\":\"Student\'s view - correct answer selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/summary-05.png?itok=tVHuEHqv\",\"alt\":\"Student\'s view - finish\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/summary','https://h5p.org/tutorial-summary','[\"summary\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(25,'H5P.Timeline',1,1,4,1,0,'Timeline','Create a timeline of events with multimedia','This is Timeline.js developed by Knight Lab, packaged as an H5P content type in order to make timelines easily editable, shareable and reuseable. The Timeline content type allows you to place a sequence of events in a chronological order. For each event you may add images and texts. You may also include assets from Twitter, YouTube, Flickr, Vimeo, Google Maps and SoundCloud.','https://h5p.org/sites/default/files/timeline.svg',1488380771,1491481225,0,16,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-01.png?itok=6BO75od2\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-02.png?itok=-SsDe4Zb\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/timeline-03.png?itok=Fmbxyxix\",\"alt\":\"Student\'s view - full screen preview\"}]','{\"id\":\"MPL2\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/timeline','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/timeline','[\"timeline\",\"slider\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(26,'H5P.TrueFalse',1,1,3,1,9,'True/False Question','Create True/False questions','True/False Question is a simple and straightforward content type that can work by itself or be inserted into other content types such as Course Presentation. A more complex question can be created by adding an image or a video.','https://h5p.org/sites/default/files/true%20false.svg',1488380892,1493286877,0,25,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-01.png?itok=LKXT7ZAG\",\"alt\":\"Student\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-02.png?itok=5AvPOokc\",\"alt\":\"Student\'s view - correct answer selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-03.png?itok=YCVfBTig\",\"alt\":\"Student\'s view - incorrect answer selected\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/true-false-04.png?itok=88ogDrvt\",\"alt\":\"Student\'s view - example without the photo\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/true-false','','[\"true\",\"false\"]','[\"Multimedia\",\"Questions\"]','Joubel'),(27,'H5P.TwitterUserFeed',1,0,7,1,0,'Twitter User feed','Show your Twitter feed with H5P','Add a Twitter user feed to your page. A great way to give your page some social integration and allow more of your users connect with you on Twitter.','https://h5p.org/sites/default/files/tweeter%20feed.svg',1488381430,1491481318,0,28,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/twitter-user-feed-01.png?itok=FAUcyut3\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/twitter-user-feed-02.png?itok=JKd0hBOm\",\"alt\":\"User\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/twitter-user-feed','https://h5p.org/documentation/content-author-guide/tutorials-for-authors/twitter-user-feed','[\"twitter\",\"embed\"]','[\"Social media\"]','Joubel'),(28,'H5P.ImageHotspots',1,5,0,1,12,'Image Hotspots','Create an image with multiple info hotspots','Image hotspots makes it possible to create an image with interactive hotspots. When the user presses a hotspot, a popup containing a header and text or video is displayed. Using the H5P editor, you may add as many hotspots as you like.','https://h5p.org/sites/default/files/image%20hotpots.svg',1491400692,1491482821,0,6,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-01.png?itok=-GthrRP1\",\"alt\":\"View - hotspots\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-02.png?itok=AP-FWcHh\",\"alt\":\"View - video as a hotspot\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-hotspots-03.png?itok=Zt1V7UYE\",\"alt\":\"View - text as a hotspot\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/image-hotspots','','[\"image\",\"hotspot\"]','[\"Multimedia\"]','Joubel'),(29,'H5P.ImageMultipleHotspotQuestion',1,0,0,1,6,'Find Multiple Hotspots','Create many hotspots for users to find','A free HTML5 based question type allowing creatives to create an image based test where the learner is to find the correct spots on an image. Use this content type with the H5P plugin for WordPress, Moodle or Drupal to challenge your users.','https://h5p.org/sites/default/files/find%20multiple%20hotspots.svg',1491482530,1491485320,0,36,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-01.png?itok=Gbi-unNy\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-02.png?itok=ctq2RdVL\",\"alt\":\"Authoring experience - adding hotspots\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/multiple-hotspots-03.png?itok=uAshALHJ\",\"alt\":\"User\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/find-multiple-hotspots','','[]','[]','lukemuller'),(30,'H5P.ImageJuxtaposition',1,1,0,1,0,'Image Juxtaposition','Create interactive images','A free HTML5-based image content type that allows users to compare two images interactively. Tell your image stories with H5P and Image Juxtaposition on WordPress, Moodle or Drupal.','https://h5p.org/sites/default/files/before-after-image_0.svg',1491482694,1492520808,0,37,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-juxtaposition-01.png?itok=EmBa43Us\",\"alt\":\"User\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/image-juxtaposition-02.png?itok=idPJFSr2\",\"alt\":\"User\'s view\"}]','{\"id\":\"MPL2\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/image-juxtaposition','','[]','[]','otacke'),(31,'H5P.Audio',1,2,9,1,4,'Audio','Upload an audio recording','Upload an audio recording in .mp3, .wav, .ogg or provide the link for an audio recording.','https://h5p.org/sites/default/files/audio.svg',1491918006,1494250982,0,15,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-01.png?itok=z6wayrwd\",\"alt\":\"End user\'s view\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-02.png?itok=NdFP2pcq\",\"alt\":\"End user\'s view\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/audio','','[]','[\"Multimedia\"]','Joubel'),(32,'H5P.AudioRecorder',1,0,3,1,12,'Audio Recorder','Create an audio recording','An HTML5 audio recorder. Record your voice and play back or download a .wav file of your recording. Use the H5P plugin to create the H5P Audio Recorder to your Drupal, Wordpress or Moodle site. ','https://h5p.org/sites/default/files/icon_1.svg',1493377972,1493380955,1,38,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-1.png?itok=vAcuqLSA\",\"alt\":\"Start screen for audio recorder\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-2.png?itok=qoLWgAaq\",\"alt\":\"Recording screen for audio recorder\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/audio-recorder-3.png?itok=EnH5rwO4\",\"alt\":\"Finish screen for audio recorder\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/audio-recorder','','[\"audio\",\"recorder\"]','[\"Multimedia\"]','Joubel'),(33,'H5P.SpeakTheWords',1,0,0,1,12,'Speak the Words','Answer a question using your voice (chrome only)','Speak the Words is only supported in browsers that implements the Web Speech API (Chrome browsers, except on iOS). You need a microphone to answer the question. Ask a question to users and make them answer using their voice. You can choose multiple correct answers. The user will be able to see what his words were interpreted as, and how close it was to the correct answers.','https://h5p.org/sites/default/files/speak%20the%20words_0.svg',1493897505,1493900421,0,41,'[{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/QuestionPagev2.png?itok=WYSV9ON0\",\"alt\":\"Question text and answer with microphone screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/ShowSolutionPagev2_0.png?itok=vUTAG0RT\",\"alt\":\"Interpreted and correct answers screen\"},{\"url\":\"https:\\/\\/h5p.org\\/sites\\/default\\/files\\/styles\\/h5p-content-type-hub-screenshot\\/public\\/CorrectSolutionPage.png?itok=poT-tQ6Z\",\"alt\":\"Correct answer screen\"}]','{\"id\":\"MIT\",\"attributes\":{\"useCommercially\":true,\"modifiable\":true,\"distributable\":true,\"sublicensable\":true,\"canHoldLiable\":false,\"mustIncludeCopyright\":true,\"mustIncludeLicense\":true}}','https://h5p.org/node/72682','','[\"language\",\"translation\",\"voice\",\"recognition\",\"speech\",\"mic\",\"microphone\"]','[\"Questions\"]','thomasmars');
